import { Avatar, Box, withStyles } from "@material-ui/core";
import React from "react";
import useLocalState from "../../../../custom-hooks/useLocalState";

interface IProps {
  postId: string;
}

const CommentBox: React.FC<IProps> = ({ postId }) => {
  const [user] = useLocalState("user", "");

  return (
    <Container>
      <Avatar
        src={user?.picture}
        style={{
          width: "41px",
          height: "41px",
          borderRadius: "50%",
          marginRight: "16px",
        }}
      />
      <input
        type="text"
        placeholder="Tell us what you think about this post..."
        style={{
          width: "80%",
          padding: "12px",
          marginRight: "16px",
          border: "1px solid black",
          borderRadius: "25px",
        }}
      />{" "}
      <Avatar
        src="https://img.icons8.com/fluent-systems-filled/30/000000/sent.png"
        style={{
          width: "2rem",
          height: "2rem",
        }}
      />
    </Container>
  );
};

export default CommentBox;

const Container = withStyles({
  root: {
    width: "100%",
    display: "flex",
    marginTop: "2rem",
    alignItems: "center",
  },
})(Box);
