import { Avatar, Box, CircularProgress, withStyles } from "@material-ui/core";
import useQuizInfo from "./useQuizInfo";

interface IProps {
  quizId: number;
}

export default function QuizInfo({ quizId }: IProps) {
  const { testStatus, quizInfo, userInfo, isLoading } = useQuizInfo(quizId);

  return (
    <Container>
      {isLoading && (
        <LoadingContainer>
          <CircularProgress color="primary" />
        </LoadingContainer>
      )}
      <ImageContainer>
        <Avatar src={`${quizInfo?.image}`} variant="square" style={{ height: 100, width: 100 }} />
      </ImageContainer>
    </Container>
  );
}

const Container = withStyles({
  root: {
    padding: "1rem",
    display: "flex",
    flexDirection: "column",
  },
})(Box);

const LoadingContainer = withStyles({
  root: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
  },
})(Box);

const ImageContainer = withStyles({
  root: {},
})(Box);
