import React, { useEffect } from "react";
import { makeStyles, Typography } from "@material-ui/core";

import { GoogleLogin } from "react-google-login";
import axios from "axios";

import { backend_url } from "../../../../../../url";

import post from "../../../../../../UtilFunctions/post";
import useLocalState from "../../../../../../custom-hooks/useLocalState";

import Image from "next/image";

const useStyles = makeStyles({
  root: {
    color: "white",
    fontSize: "small",
    textTransform: "capitalize",
    cursor: "pointer",
    marginLeft: "auto",
    marginRight: 20,
    "&:hover": {
      color: "black",
    },
  },
  userContainer: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    width: 150,
    color: "white",
    textTransform: "lowercase",
  },
  profilePic: {
    borderRadius: "50%",
    // overflow: "hidden",
    height: 40,
    width: 40,
  },
  userName: {
    textTransform: "capitalize",
  },
});

const Login: React.FC<{}> = () => {
  const [user, setUser] = useLocalState<any>("user", "");
  const classes = useStyles();

  const responseGoogle = async (googleUser) => {
    if (!googleUser.error) {
      const id_token = googleUser.tokenId;
      const google_id = parseInt(googleUser.profileObj.googleId);

      post(
        `${backend_url}/api/auth/google/verify`,
        { id_token: id_token, google_id: google_id },
        (response) => {
          setUser(response.data);
        },
        () => {}
      );
    }
  };

  console.log(user.image_url);

  return (
    <>
      {!user ? (
        <GoogleLogin
          clientId="540083931812-7bt05mqtrriog1f1kdpvotbvnr01iien.apps.googleusercontent.com"
          buttonText="Login"
          onSuccess={responseGoogle}
          onFailure={responseGoogle}
          cookiePolicy={"single_host_origin"}
        />
      ) : (
        <div className={classes.userContainer}>
          <div className={classes.profilePic}>
            <Image src={user.image_url} width={"100%"} height={"100%"} />
          </div>
          <Typography variant="caption" className={classes.userName}>
            {user.name}
          </Typography>
        </div>
      )}
    </>
  );
};

export default Login;
