import { Box, Radio, withStyles } from "@material-ui/core";
import { useContext } from "react";
import { QuizTestStateContext } from "../../../../Questions";
import { QuestionContext } from "../../../SingleQuestion";
import useOption from "./useOption";

interface IProps {
  option: any;
}

export default function Option({ option }: IProps) {
  const { answers, setAnswers } = useContext(QuizTestStateContext);
  const { question } = useContext(QuestionContext);

  const {isChecked, handleClick} = useOption(option?.id, );

  return (
    <Container>
      <Radio checked={true} />
    </Container>
  );
}

const Container = withStyles({
  root: {
    borderTop: "1px solid gray",
    padding: "0.5rem",
    display: "flex",
    alignItems: "center",
  },
})(Box);
