import { Box, Slide, withStyles } from "@material-ui/core";
import { useEffect, useState } from "react";
import IQuestion from "../../../../Models/IQuestion";

interface IProps {
  index: number;
  question: IQuestion;
  currentQuestionIndex: number;
}

export default function Question({
  question,
  currentQuestionIndex,
  index,
}: IProps) {
  const [isVisible, setIsVisible] = useState(index == currentQuestionIndex);
  const [direction, setDirection] = useState<"left" | "right">("right");

  useEffect(() => {
    // console.log(
    //   `${question?.id} is ${
    //     isVisible ? "visible" : "not visible"
    //   } --- current: ${currentQuestionId}`
    // );
    if (currentQuestionIndex > index) {
      setDirection("right");
    } else if (currentQuestionIndex < index) {
      setDirection("left");
    }

    setIsVisible(index == currentQuestionIndex);
  }, [currentQuestionIndex]);

  return (
    <Slide
      in={isVisible}
      direction={direction}
      timeout={{ enter: 1500, exit: 1500 }}
      mountOnEnter
      unmountOnExit
    >
      <Container>{`${question?.id}.${question?.question}`}</Container>
    </Slide>
  );
}

const Container = withStyles({
  root: {
    padding: "0.5rem 1rem",
  },
})(Box);
