import { Box, withStyles } from "@material-ui/core";
import React, { useContext, useState } from "react";
import IPostCommentReply from "../../../../../../Models/IPostCommentReply";
import { CommentContext } from "../Comment";
import Reply from "./Reply/Reply";

interface IProps {
  setShowReply: Function;
}

const RepliesSection: React.FC<IProps> = ({ setShowReply }) => {
  const { comment } = useContext(CommentContext);
  const [replies, setReplies] = useState<IPostCommentReply[] | []>(
    comment?.replies
  );

  const handleClick = () => {
    setShowReply((state: boolean) => !state);
  };
  return (
    <Container onClick={handleClick}>
      {replies &&
        replies.map((reply) => {
          return <Reply reply={reply} />;
        })}
    </Container>
  );
};

export default RepliesSection;

const Container = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
  },
})(Box);
