import { Box, Typography, withStyles } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import IImage from "../../../../Models/IImage";

import Image from "next/image";
import { backend_url, local_backend_url } from "../../../../url";

interface IProps {
  title: string;
  image: IImage;
}

const Title: React.FC<IProps> = ({ title, image }) => {
  const [imageUrl, setImageUrl] = useState<string>(image.image_link);

  useEffect(() => {
    setImageUrl(image.image_link);
  }, []);

  return (
    <Container>
      <ImageContainer>
        <Image
          alt="image"
          src={`${local_backend_url}/storage/${imageUrl}`}
          height={"100%"}
          width={"100%"}
          layout="responsive"
        />
      </ImageContainer>
      <TextContainer>
        <Typography>{title}</Typography>
      </TextContainer>
    </Container>
  );
};

export default Title;

const Container = withStyles({
  root: {
    width: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-around",
  },
})(Box);

const ImageContainer = withStyles({
  root: {
    height: 100,
    width: 100,
    borderRadius: 5,
    overflow: "hidden",
    flexShrink: 0,
  },
})(Box);

const TextContainer = withStyles({
  root: {
    maxWidth: "50ch",
  },
})(Box);
