import {
  ListItem,
  ListItemIcon,
  ListItemText,
  makeStyles,
} from "@material-ui/core";

import Image from "next/image";
import useLocalState from "../../../../../../../custom-hooks/useLocalState";
import { ExpandLess, ExpandMore } from "@material-ui/icons";

const useStyles = makeStyles({
  userContainer: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    width: 200,
    color: "white",
    textTransform: "capitalize",
  },
  profilePic: {
    borderRadius: "50%",
    overflow: "hidden",
    height: 40,
    width: 40,
  },
  userName: {

  }
});

const User: React.FC<{}> = () => {
  const [user, setUser] = useLocalState<any>("user", "");
  const classes = useStyles();

  return (
    <div className={classes.userContainer}>
      <ListItem button>
        <ListItemIcon>
          <div className={classes.profilePic}>
            <Image src={user.image_url} width={40} height={40} />
          </div>
        </ListItemIcon>
        <ListItemText primary={`${user.name.toLowerCase()}`} className={classes.userName} />
        {open ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      {/* <div className={classes.profilePic}>
      </div>
      <Typography variant="caption">{user.name.toLowerCase()}</Typography> */}
    </div>
  );
};

export default User;
