import { Avatar, Box, withStyles } from "@material-ui/core";
import React from "react";

import texts from "../../../../texts/frontpage";

interface IProps {}

const Testimonials: React.FC<IProps> = () => {
  return (
    <Container>
      {texts.testimonials.map((testimonial) => {
        return (
          <TestimonialContainer>
            <Message>{testimonial?.message}</Message>
            <Person>
              <div></div>
              <div>{testimonial?.person?.name}</div>
            </Person>
          </TestimonialContainer>
        );
      })}
    </Container>
  );
};

export default Testimonials;

const Container = withStyles({
  root: {
    margin: "4rem 5%",
    display: "flex",
    flexWrap: "wrap",
    alignItems: "stretch",
    justifyContent: "space-between",
    color: "#595858",
  },
})(Box);

const TestimonialContainer = withStyles({
  root: {
    backgroundColor: "white",
    position: "relative",
    width: "24%",
    borderRadius: 20,
    padding: "1rem",
    border: "1px solid #707070",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
})(Box);

const Message = withStyles({
  root: {
    color: "#595858",
    lineHeight: 1.6,
  },
})(Box);

const Person = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
  },
})(Box);
