import { Box, withStyles } from "@material-ui/core";
import React from "react";

interface IProps {
  isOpen: boolean;
  handleClose: Function;
}

const Dialog: React.FC<IProps> = ({ isOpen, handleClose }) => {
  return (
    <>
      {isOpen && (
        <>
          <Backdrop onClick={() => handleClose()}></Backdrop>
          <Modal></Modal>
        </>
      )}
    </>
  );
};

export default Dialog;

const Backdrop = withStyles({
  root: {
    position: "fixed",
    top: 0,
    left: 0,
    bottom: 0,
    right: 0,
    backgroundColor: "black",
    opacity: 0.5,
    zIndex: 10000,
  },
})(Box);

const Modal = withStyles({
  root: {
    opacity: 1,
    zIndex: 20000,
    position: "fixed",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "100% auto",
    backgroundColor: "white",
    padding: "2rem 2rem"
  },
})(Box);
