import React from 'react';

import parse from "html-react-parser";

interface IProps {
    body: string;
}


const PostBody:React.FC<IProps> = ({ body }) => {
    const el = parse(`${body}`, {
        replace
    });

    return <div>
        {el}
    </div>
}

export default PostBody;
