import React from "react";

import { Avatar, Box, makeStyles, Typography, withStyles } from "@material-ui/core";
import IOption from "../../../../Models/IOption";

import { local_backend_url } from "../../../../url";

interface IProps {
  option: IOption;
  position: number;
  isChecked?: boolean;
}

const Option: React.FC<IProps> = ({ option, position, isChecked }) => {
  return (
    <Container>
      {option.image && (
        <ImageContainer>
          <Avatar
            alt={`${option.title}`}
            src={`${local_backend_url}/storage/${option.image.image_link}`}
            style={{ width: "100%", height: "100%" }}
            variant="square"
          />
        </ImageContainer>
      )}
      {option.title && (
        <TextContainer>
          <Typography>{`${String.fromCharCode(position + 64)}. ${
            option.title
          }`}</Typography>
        </TextContainer>
      )}
      {isChecked && "checked"}
    </Container>
  );
};

export default Option;

const useStyles = makeStyles({
  container: {

  }
})

const Container = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    maxWidth: "25vw",
    minWidth: 200,
    height: "100%",
    cursor: "pointer",
    background:
      "transparent linear-gradient(180deg, #995FD4 0%, #1F29356E 100%) 0% 0% no-repeat padding-box",
    borderRadius: 10,
    overflow: "hidden",
    marginTop: "1rem",
    ["@media (max-width: 600px)"]: {
      maxWidth: "100%",
    },
  },
})(Box);

const ImageContainer = withStyles({
  root: {
    width: "100%",
  },
})(Box);

const TextContainer = withStyles({
  root: {
    textAlign: "center",
  },
})(Box);
