import { Box, Typography, withStyles } from "@material-ui/core";
import IQuiz from "../../../Models/IQuiz";
import ITest from "../../../Models/ITest";

interface IProps {
  test: ITest;
  quiz: IQuiz;
}

export default function TestInfo({ test, quiz }: IProps) {
  const assessmentOrQuiz = quiz?.is_assessment ? "assessment" : "quiz";

  return (
    <Container>
      <Title variant="h5">{`${quiz?.title} ${assessmentOrQuiz}`}</Title>
      <Divider />
      <HasPassedText variant="h6">
        {test?.has_passed
          ? `Congrats, you have passed the ${assessmentOrQuiz}!`
          : `Sorry, you have not passed the ${assessmentOrQuiz}.`}
      </HasPassedText>
      <ScoreContainer>
        <Typography variant="subtitle1">
          Score: <b>{`${test?.percentage}%`}</b>
        </Typography>
        <Typography variant="subtitle1">
          Total Questions: <b>{`${quiz?.total_questions_count}`}</b>
        </Typography>
        <Typography variant="subtitle1">
          Right Answers: <b>{`${test?.correct_answers_}`}</b>
        </Typography>
      </ScoreContainer>
    </Container>
  );
}

const Container = withStyles({
  root: {
    width: "100%",
    border: "1px solid #e6e6e4",
    borderRadius: "7px 7px 0px 0px",
    backgroundColor: "white",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
})(Box);

const Title = withStyles({
  root: {
    padding: "0.7rem",
    textTransform: "capitalize",
  },
})(Typography);

const Divider = withStyles({
  root: {
    width: "100%",
    height: 2,
    backgroundColor: "#e6e6e4",
  },
})(Box);

const HasPassedText = withStyles({
  root: {
    fontWeight: 500,
    margin: "1rem",
  },
})(Typography);

const ScoreContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    padding: "1rem",
    border: "1px solid lightgray",
    borderRadius: 7,
    marginBottom: "1rem",
    width: "min(600px, 97%)",
  },
})(Box);
