import { Box, Snackbar, withStyles } from "@material-ui/core";
import axios from "axios";
import React, { useEffect, useState } from "react";
import useLocalState from "../../../../../custom-hooks/useLocalState";
import IPost from "../../../../../Models/IPost";
import { backend_url } from "../../../../../url";

interface IProps {
  post: IPost;
}

const Likes: React.FC<IProps> = ({ post }) => {
  const [user] = useLocalState("user", "");
  const [showLoginAlert, setShowLoginAlert] = useState<boolean>(false);

  const handleClick = () => {
    if (!user.token) {
      setShowLoginAlert(true);
      setTimeout(() => {
        setShowLoginAlert(false);
      }, 3000);

      return;
    }
  };

  useEffect(() => {
    if (user.token) {
      axios
        .get(`${backend_url}/api/posts/${post.slug}/is-liked-by-user`, {
          headers: {
            Accept: "application/json",
            Authorization: `Bearer ${user.token}`,
          },
        })
        .then((response) => {
          console.log(resopnse.data);
        });
    }
  }, []);

  return (
    <Container onClick={handleClick}>
      {post.likes_count ? (
        <>
          <Number>{post?.likes_count}</Number>
          <p>Like{post?.likes_count > 1 ? "s" : ""}</p>
        </>
      ) : (
        <>
          <Number>0</Number>
          <p>Like</p>
        </>
      )}
      <LoginAlert
        key={post.id}
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
        open={showLoginAlert}
        message="Please Login To Like The Post."
      />
    </Container>
  );
};

export default Likes;

const Container = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
    cursor: "pointer",
  },
})(Box);

const Number = withStyles({
  root: {
    display: "inline-block",
    marginRight: 8,
  },
})(Box);

const LoginAlert = withStyles({
  root: {
    zIndex: 20000,
  },
})(Snackbar);
