import {
  Box,
  CircularProgress,
  Typography,
  withStyles,
} from "@material-ui/core";
import useBadge from "./useBadge";

interface IProps {
  testId: number;
}

export default function Badge({ testId }: IProps) {
  const { isLoading } = useBadge(testId);

  return (
    <Container>
      <Title variant="h5">{`${quiz?.title} ${assessmentOrQuiz}`}</Title>
      <Divider />
      {isLoading && (
        <LoadingContainer>
          <CircularProgress />
          <Typography variant="subtitle1">
            Getting Your Badge Ready. Please wait.
          </Typography>
        </LoadingContainer>
      )}
    </Container>
  );
}

const Container = withStyles({
  root: {
    width: "100%",
    border: "1px solid #e6e6e4",
    borderRadius: "7px 7px 0px 0px",
    backgroundColor: "white",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: "1rem",
  },
})(Box);

const LoadingContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "1rem",
  },
})(Box);
