import {
  Box,
  Divider,
  ListItem,
  ListItemIcon,
  ListItemText,
  Typography,
  withStyles,
} from "@material-ui/core";
import { BarChartOutlined, InfoOutlined } from "@material-ui/icons";

export default function UserDetail({ userInfo }) {
  return (
    <Container>
      <Heading variant="h6">Your Attempts</Heading>

      <UserDetailContainer>
        {userInfo?.attempts_count && (
          <SingleUserDetail>
            <InfoOutlined />
            <UserDetailText>
              You have attempted the quiz <b>{userInfo?.attempts_count}</b>{" "}
              times.
            </UserDetailText>
          </SingleUserDetail>
        )}
        {userInfo?.best_attempt && (
          <SingleUserDetail>
            <BarChartOutlined />
            <UserDetailText>
              Your best score is <b></b>{userInfo?.best_attempt?.percentage}
            </UserDetailText>
          </SingleUserDetail>
        )}
      </UserDetailContainer>

      <Divider />
    </Container>
  );
}

const Container = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
  },
})(Box);

const Heading = withStyles({
  root: {
    fontWeight: 600,
    margin: "0.5rem 0 0 0",
  },
})(Typography);

const UserDetailContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    margin: "1rem 0",
  },
})(Box);

const SingleUserDetail = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
    margin: "0.3rem 0",
  },
})(Box);

const UserDetailText = withStyles({
  root: {
    margin: "0 0 0 0.5rem",
    whiteSpace: "nowrap",
  },
})(Typography);
