import { GetStaticProps } from "next";
import React from "react";
import axios from "axios";

import PostsComponent from "../../Components/Posts/Posts";

import {local_backend_url} from "../../url";

export const getStaticProps: GetStaticProps = async (context) => {
  const posts = await axios.get(local_backend_url + "/canvas-blog/api/posts");
  const topics = await axios.get(`${local_backend_url}/canvas-blog/api/topics`);

  return {
    props: {
      posts: posts.data.data,
      topics: topics.data,
    },
  };
};

const Posts = ({ posts, topics }) => {

  return <PostsComponent posts={posts} />;
};

export default Posts;
