import { makeStyles } from "@material-ui/core";
import React from "react";
import IPost from "../../../../Models/IPost";

interface IProps {
  post: IPost;
  position: number;
}

const colors: string[] = [
  "#97A5B8",
  "#9C95FD",
  "#F493FF",
  "#28E36E",
  "#FFE533",
];

const useStyles = makeStyles({
  container: {
    display: "flex",
    flexDirection: "column",
  },
  topic: {
    color: "white",
    width: 206,
    borderRadius: 10,
    textAlign: "center",
    textTransform: "capitalize",
    paddingBlock: 3,
    backgroundColor: (props: { position: number }) => {
      const {position} = props;
      return colors[(position % colors.length)];
    },
    whiteSpace: "nowrap",
    overflow: "hidden",
    textOverflow: "ellipsis",
    paddingInline: 3,
  },
});

const Post: React.FC<IProps> = ({ post, position }) => {
  const classes = useStyles({ position });

  return (
    <div className={classes.container}>
      <div className={classes.topic}>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Laborum, earum fugit corporis veniam explicabo eos ad eum! Enim quas nisi suscipit, officiis, id, rem adipisci quidem ab facilis modi iusto tenetur! Dolorem ipsum iusto accusantium incidunt culpa eaque, tempora minima!</div>
    </div>
  );
};

export default Post;
