import {
  Avatar,
  Box,
  CircularProgress,
  Divider,
  Typography,
  withStyles,
} from "@material-ui/core";
import { backend_url } from "../../../../../../url";
import BottomBar from "./BottomBar/BottomBar";
import useQuizInfo from "./useQuizInfo";

import { QueryBuilder, QuestionAnswerOutlined } from "@material-ui/icons";
import getFormattedTime from "../../../../../../UtilFunctions/getFormattedTime";

interface IProps {
  quizId: number;
}

export default function QuizInfo({ quizId }: IProps) {
  const { testStatus, quizInfo, userInfo, isLoading } = useQuizInfo(quizId);

  const times = getFormattedTime(quizInfo?.duration);
  const quizOrAssessment = quizInfo?.is_assessment ? "assessment" : "quiz";

  return (
    <Container>
      {isLoading && (
        <LoadingContainer>
          <CircularProgress color="primary" />
        </LoadingContainer>
      )}
      <ImageContainer>
        <Avatar
          src={`${backend_url}/storage/${quizInfo?.image_link}`}
          variant="square"
          style={{ height: 100, width: 100 }}
        />
      </ImageContainer>
      {/* <Title variant="h5">{quizInfo?.title?.toLowerCase()}</Title> */}
      <Description variant="subtitle1">
        {quizInfo?.description?.toLowerCase()}
      </Description>
      <TestTakerCount variant="subtitle2">
        {quizInfo?.test_taker_count ?? "0"} people{" "}
        {quizInfo?.test_taker_count
          ? quizInfo?.test_taker_count == 1
            ? "has"
            : "have"
          : "have"}{" "}
        taken the {quizInfo?.is_assessment ? "assessment" : "quiz"}
      </TestTakerCount>
      <QuizDetailContainer>
        <QuizDetail>
          <QuestionAnswerOutlined />
          <QuizDetailText variant="subtitle1">
            <b>{quizInfo?.questions_count}</b> questions
          </QuizDetailText>
        </QuizDetail>
        <QuizDetail>
          <QueryBuilder />
          <QuizDetailText variant="subtitle1">
            <b>{`${
              times?.hours
                ? times?.hours > 1
                  ? times?.hours + " hours"
                  : times?.hours + " hour"
                : ""
            } ${
              times?.minutes
                ? times?.minutes > 1
                  ? times?.minutes + " minutes"
                  : times?.minutes + " minute"
                : ""
            } ${
              times?.seconds
                ? times?.seconds > 1
                  ? times?.seconds + "seconds"
                  : times?.seconds + "seond"
                : ""
            }`}</b>{" "}
            to complete the {quizOrAssessment}
          </QuizDetailText>
        </QuizDetail>
        <QuizDetail>
          <QuestionAnswerOutlined />
          <QuizDetailText variant="subtitle1">
            Score atleast <b>{quizInfo?.passing_percentage}%</b> to pass the{" "}
            {quizOrAssessment}
          </QuizDetailText>
        </QuizDetail>
      </QuizDetailContainer>
      <Divider />
      <BottomBar testStatus={testStatus} />
    </Container>
  );
}

const Title = withStyles({
  root: {
    margin: "1rem 0 0 0",
    textTransform: "capitalize",
    fontWeight: 600,
  },
})(Typography);

const Description = withStyles({
  root: {
    margin: "0.2rem 0",
    textTransform: "capitalize",
  },
})(Typography);

const TestTakerCount = withStyles({
  root: {
    margin: "0.2rem 0",
    color: "gray",
  },
})(Typography);

const Container = withStyles({
  root: {
    padding: "1rem",
    display: "flex",
    flexDirection: "column",
  },
})(Box);

const LoadingContainer = withStyles({
  root: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
  },
})(Box);

const ImageContainer = withStyles({
  root: {},
})(Box);

const QuizDetailContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    margin: "1rem 0",
  },
})(Box);

const QuizDetail = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
  },
})(Box);

const QuizDetailText = withStyles({
  root: {
    margin: "0 0 0 0.5rem",
  },
})(Typography);
