import { makeStyles, Typography, withStyles } from "@material-ui/core";
import React from "react";
import IPost from "../../../../Models/IPost";

interface IProps {
  post: IPost;
  position: number;
}

const colors: string[] = [
  "#97A5B8",
  "#9C95FD",
  "#F493FF",
  "#28E36E",
  "#FFE533",
];

const useStyles = makeStyles({
  container: {
    display: "flex",
  },
  topic: {
    color: "white",
    cursor: "pointer",
    width: 206,
    borderRadius: 10,
    textAlign: "center",
    textTransform: "capitalize",
    paddingBlock: 3,
    backgroundColor: (props: { position: number }) => {
      const { position } = props;
      if (position > colors.length) {
        return colors[position % colors.length];
      }
      return colors[position];
    },
    whiteSpace: "nowrap",
    overflow: "hidden",
    textOverflow: "ellipsis",
    paddingInline: 3,
  },
  leftContainer: {},
  rightContainer: {},
});

const Post: React.FC<IProps> = ({ post, position }) => {
  const classes = useStyles({ position });

  return (
    <div className={classes.container}>
      <div className={classes.leftContainer}>
        <div className={classes.topic}>
          <Typography>{post.topic[0].name}</Typography>
        </div>
        <div>
          <PostTitle>{post.title}</PostTitle>
          <PostSummary>{`${post.summary.substr(
            0,
            1000
          )} [...][...]`}</PostSummary>
        </div>
      </div>
      <div className={classes.rightContainer}>
        <PostAuthor>{post.user.name}</PostAuthor>
      </div>
    </div>
  );
};

export default Post;

const PostTitle = withStyles({
  root: {
    color: "#3798A7",
    fontSize: 30,
    marginBlock: 15,
    textTransform: "capitalize",
    cursor: "pointer",
  },
})(Typography);

const PostSummary = withStyles({
  root: {
    color: "#686868",
    fontSize: 15,
    marginBlock: 15,
    textTransform: "capitalize",
    cursor: "pointer",
  },
})(Typography);

const PostAuthor = withStyles({
  root: {
    whiteSpace: "nowrap",
  },
})(Typography);
