import React, { useContext, useEffect, useState } from "react";

import { Box, Typography, withStyles } from "@material-ui/core";
import IOption from "../../../../Models/IOption";

import Image from "next/image";
import { local_backend_url } from "../../../../url";
import useOptionClicked, {
  getIsChecked,
} from "../../../../custom-hooks/useOptionClicked";
import { QuestionContext } from "../../../Questions/Questions";
import { QuizIDContext } from "../../../QuizTest/QuizTest";
import useLocalState from "../../../../custom-hooks/useLocalState";

interface IProps {
  option: IOption;
  position: number;
}

const Option: React.FC<IProps> = ({ option, position }) => {
  const { questionID, answerType } = useContext(QuestionContext);
  const { quizID } = useContext(QuizIDContext);

  const [handleClick] = useOptionClicked(
    option.id,
    questionID,
    quizID,
    answerType
  );

  const [answers] = useLocalState(`${user.email}`)

  const [isChecked, setIsChecked] = useState<boolean>(
    getIsChecked(answers, questionID, option.id)
  );

  useEffect(() => {
    console.log("answer have changed");
  }, [answers]);

  return (
    <Container onClick={() => handleClick()}>
      {option.image && (
        <ImageContainer>
          <Image
            alt="image"
            src={`${local_backend_url}/storage/${option.image.image_link}`}
            height={"100%"}
            width={"100%"}
            layout="responsive"
          />
        </ImageContainer>
      )}
      {option.title && (
        <TextContainer>
          <OptionButton />
          <Typography>
            {`${String.fromCharCode(position + 64)}. ${option.title}`}
          </Typography>
        </TextContainer>
      )}
    </Container>
  );
};

export default Option;

const Container = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
    marginBottom: "1rem",
    cursor: "pointer",
  },
})(Box);

const ImageContainer = withStyles({
  root: {
    height: 100,
    width: 100,
    borderRadius: 5,
    marginRight: "2rem",
    overflow: "hidden",
    flexShrink: 0,
  },
})(Box);

const TextContainer = withStyles({
  root: {
    background:
      "transparent linear-gradient(180deg, #995FD4 0%, #1F29356E 100%) 0% 0% no-repeat padding-box",
    width: "100%",
    padding: "1rem 1rem",
    borderRadius: 10,
    display: "flex",
    alignItems: "center",
  },
})(Box);

const OptionButton = withStyles({
  root: {
    padding: "0.7rem",
    backgroundColor: "#17ABC2",
    borderRadius: "50%",
    overflow: "hidden",
    marginRight: "1rem",
  },
})(Box);
