import { useState, useEffect } from "react";
import Cookies from "js-cookie";

const setCookie = (key, value) => {
  Cookies.set(
    key ? JSON.stringify(key) : key,
    value ? JSON.stringify(value) : value
  );
};

const getCookie = (key) => {
  const result = Cookies.get(JSON.stringify(key));
  return result ? JSON.parse(result) : result;
};

function useLocalState<T>(key: string, defaultValue: T) {
  const [value, setValue] = useState(() => {
    let result = null;
    if (process.browser) {
      const storedValue = localStorage.getItem(key)
        ? JSON.parse(localStorage.getItem(key))
        : localStorage.getItem(key);

      if (!storedValue) {
        result = defaultValue;
      }
      result = storedValue === null ? defaultValue : storedValue;
    } else {
      result = defaultValue;
    }

    // console.log("check: ");
    // console.log("local storage : ");
    // console.log(result);
    // console.log("cookie: ");
    // console.log(getCookie(key));

    return result;
  });

  useEffect(() => {
    const listener = (e) => {
      if (e.storageArea === localStorage && e.key === key) {
        setValue(JSON.parse(e.newValue));
      }
    };
    window.addEventListener("storage", listener);

    return () => {
      window.removeEventListener("storage", listener);
    };
  }, [key]);

  const setValueInLocalStorage = (newValue) => {
    setValue((currentValue) => {
      if (typeof newValue === "function") {
        setCookie(key, newValue(currentValue));
      } else {
        setCookie(key, newValue);
      }
      const result =
        typeof newValue === "function" ? newValue(currentValue) : newValue;
      localStorage.setItem(key, JSON.stringify(result));
      return result;
    });
  };

  useEffect(() => {
    const storedValue = JSON.parse(localStorage.getItem(key));
    if (storedValue) {
      setValueInLocalStorage(storedValue);
    } else {
      setValueInLocalStorage(value);
    }
  }, []);

  return [value, setValueInLocalStorage];
}

export default useLocalState;
