import { Avatar, Box, Typography, withStyles } from "@material-ui/core";
import { useRouter } from "next/router";
import React from "react";
import useLocalState from "../../../custom-hooks/useLocalState";
import IQuiz from "../../../Models/IQuiz";
import { backend_url } from "../../../url";

interface IProps {
  quiz: IQuiz;
}

const k: React.FC<IProps> = ({ quiz }) => {
  const router = useRouter();

  const [user] = useLocalState("user", "");

  const handleClick = () => {
    if (!user.token) return;
    router.push(`/test/quiz/${quiz.id}`);
  };

  return (
    <Container>
      <Avatar
        src={`${backend_url}/storage/${quiz?.image?.image_link}`}
        alt={quiz.title}
        style={{ width: 90, height: 90 }}
      />
      <TitleButtonContainer>
        <Title>{quiz.title}</Title>
        <Button onClick={() => handleClick()}>Take the Quiz</Button>
      </TitleButtonContainer>
    </Container>
  );
};

export default Quiz;

const Container = withStyles({
  root: {
    width: "80%",
    position: "relative",
    background:
      "transparent linear-gradient(180deg, #966BC2 0%, #437BBE6E 100%) 0% 0% no-repeat padding-box",
    marginTop: 30,
    borderRadius: 15,
    padding: "40px 50px",
    ["@media (max-width: 600px)"]: {
      width: "95%",
    },
    display: "flex",
  },
})(Box);

const TitleButtonContainer = withStyles({
  root: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
    marginLeft: 50,
  },
})(Box);

const Title = withStyles({
  root: {
    fontSize: "1.2rem",
  },
})(Typography);

const Button = withStyles({
  root: {
    maxWidth: 400,
    marginTop: 20,
    cursor: "pointer",
    width: "100%",
    padding: "10px 0px",
    borderRadius: 10,
    textAlign: "center",
    background:
      "transparent linear-gradient(180deg, #995FD4 0%, #1F29356E 100%) 0% 0% no-repeat padding-box",
  },
})(Box);
