import { makeStyles, Typography } from "@material-ui/core";
import React from "react";

import Image from "next/image";
// 
  const useStyles = makeStyles({
    heroContainer: {
      width: "100vw",
      overflowX: "hidden",
      background:
        "transparent linear-gradient(180deg, #000066 0%, #032C73 57%, #00163B 100%, #003A67 100%) 0% 0% no-repeat padding-box",
      paddingTop: 100,
      paddingLeft: "5%",
      color: "white",
      position: "relative",
      ["@media (max-width: 600px)"]: {
        paddingLeft: 5,
        paddingRight: 5,
      },
    },
    contentContainer: {
      width: "40%",
      ["@media (max-width: 600px)"]: {
        width: "100%",
      },
    },
    heroText: {
      marginTop: 50,
      marginBottom: 30,
    },
    smallButtons: {
      display: "flex",
      justifyContent: "space-around",
      minWidth: 300,
      marginBottom: 40,
    },
    smallButton: {
      backgroundColor: "#0380A7D4",
      borderRadius: 50,
      width: "100%",
      textAlign: "center",
      paddingTop: 5,
      paddingBottom: 5,
      marginRight: 15,
      fontSize: "small",
    },
    bigButton: {
      display: "flex",
      alignItems: "center",
      minWidth: 300,
      marginBottom: 30,
    },
    bigButtonLogo: {
      marginLeft: 10,
    },
    bigButtonText: {
      marginLeft: "auto",
      marginRight: "auto",
    },
    heroLogo: {
      position: "absolute",
      top: 100,
      right: "20%",
      ["@media (max-width: 1000px)"]: {
        right: "15%",
      },
      ["@media (max-width: 800px)"]: {
        right: "10%",
      },
      ["@media (max-width: 700px)"]: {
        right: "5%",
      },
      ["@media (max-width: 600px)"]: {
        display: "none",
      },
    },
  });
const Hero: React.FC<{}> = () => {

  const classes = useStyles();

  return (
    <div className={classes.heroContainer}>
      <div className={classes.contentContainer}>
        <Typography component="h1" variant="h4">
          Why{" "}
          <span style={{ color: "#5AB4E2", fontWeight: "bold" }}>
            Smartifier
          </span>
        </Typography>
        <div className={classes.heroText}>
          <Typography component="h2" variant="caption">
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Quia nihil
            eum totam aspernatur voluptate, molestiae expedita aliquid dolores
            explicabo quae rerum, alias minima obcaecati nostrum maxime dolorum.
            Iusto harum cumque aut exercitationem velit impedit vel alias iste
            accusamus laudantium vero eum rerum, ex culpa, perspiciatis quis
            ipsa atque, earum quisquam.
          </Typography>
        </div>
        <div className={classes.smallButtons}>
          <div className={classes.smallButton}>Talk to us</div>
          <div className={classes.smallButton}>All Courses</div>
        </div>
        <div className={`${classes.smallButton} ${classes.bigButton}`}>
          <div className={classes.bigButtonLogo}>
            <Image
              src="/assets/small_youtube_logo.png"
              width={29}
              height={21}
            />
          </div>
          <div className={classes.bigButtonText}>
            <Typography variant="caption">
              Find free video tutorials on YouTube
            </Typography>
          </div>
        </div>
      </div>
      <div className={classes.heroLogo}>
        <Image src="/assets/awe.png" width={300} height={300} />
      </div>
    </div>
  );
};

export default Hero;
