import { Box, Typography, withStyles } from "@material-ui/core";
import IQuiz from "../../../Models/IQuiz";
import ITest from "../../../Models/ITest";

interface IProps {
  test: ITest;
  quiz: IQuiz;
}

export default function TestInfo({ test, quiz }: IProps) {
  const assessmentOrQuiz = quiz?.is_assessment ? "assessment" : "quiz";

  // const passedText = `Congratulations! You have passed the quiz on
  // ‘______________’. Do remember to share your badge on social
  // media for personal branding.`;

  const passedText = `Congratulations! You have passed the quiz on
  ‘______________’. remember to share your badge on social
  media for personal branding.`;

  const failedText = `Thank you for participating in the quiz on
  ‘____________’! Please retake the quiz and try to get your
  badge by scoring more than 80% marks.`;

  return (
    <Container>
      <Title variant="h5">{`${quiz?.title} ${assessmentOrQuiz}`}</Title>
      <Divider />
      <ScoreContainer>
        <Typography variant="subtitle1">
          Score: <b>{`${test?.percentage}%`}</b>
        </Typography>
        <Typography variant="subtitle1">
          Total Questions: <b>{`${quiz?.total_questions_count}`}</b>
        </Typography>
        <Typography variant="subtitle1">
          Right Answers: <b>{`${test?.correct_answers_count}`}</b>
        </Typography>
      </ScoreContainer>
      <HasPassedText variant="subtitle1">
        {test?.has_passed
          ? `Congrats, you have passed the ${assessmentOrQuiz}!`
          : `Sorry, you have not passed the ${assessmentOrQuiz}.`}
      </HasPassedText>
    </Container>
  );
}

const Container = withStyles({
  root: {
    width: "100%",
    border: "1px solid #e6e6e4",
    borderRadius: "7px 7px 0px 0px",
    backgroundColor: "white",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
})(Box);

const Title = withStyles({
  root: {
    padding: "0.7rem",
    textTransform: "capitalize",
  },
})(Typography);

const Divider = withStyles({
  root: {
    width: "100%",
    height: 2,
    backgroundColor: "#e6e6e4",
  },
})(Box);

const HasPassedText = withStyles({
  root: {
    fontWeight: 500,
    marginBottom: "0.5rem",
  },
})(Typography);

const ScoreContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    margin: "0.5rem 0",
    padding: "1rem",
    border: "1px solid lightgray",
    borderRadius: 7,
    width: "min(600px, 97%)",
  },
})(Box);
