import { Avatar, Box, Snackbar, withStyles } from "@material-ui/core";
import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import useLocalState from "../../../../../../../custom-hooks/useLocalState";
import { backend_url } from "../../../../../../../url";

import { CommentContext } from "../../Comment";

interface IProps {}

const Likes: React.FC<IProps> = () => {
  const { comment } = useContext(CommentContext);

  const [user] = useLocalState("user", "");
  const [showLoginAlert, setShowLoginAlert] = useState<boolean>(false);
  const [isLikedByUser, setIsLikedByUser] = useState<boolean>(false);
  const [likesCount, setLikesCount] = useState<number>(comment?.likes_count);

  const handleClick = () => {
    if (!user.token) {
      setShowLoginAlert(true);
      setTimeout(() => {
        setShowLoginAlert(false);
      }, 3000);
      return;
    }

    if (isLikedByUser) {
      unLikeComment();
    } else {
      likeComment();
    }
  };

  useEffect(() => {
    // getHasUserLikedThePost();
  }, []);

  useEffect(() => {
    // getHasUserLikedThePost();
    // if (!user.token) setIsLikedByUser(false);
  }, [user]);

  const getHasUserLikedThePost = () => {
    // if (user.token) {
    //   axios
    //     .get(`${backend_url}/api/posts/${post?.slug}/is-liked-by-user`, {
    //       headers: {
    //         Accept: "application/json",
    //         Authorization: `Bearer ${user.token}`,
    //       },
    //     })
    //     .then((response) => {
    //       setIsLikedByUser(response.data.is_liked_by_user);
    //     });
    // }
  };

  const likeComment = () => {
    if (user.token) {
      axios
        .post(
          `${backend_url}/api/post/like`,
          {
            comment_id: comment?.id,
          },
          {
            headers: {
              Accept: "application/json",
              Authorization: `Bearer ${user.token}`,
            },
          }
        )
        .then((response) => {
          setIsLikedByUser(true);
          setLikesCount((count) => count + 1);
        });
    }
  };

  const unLikeComment = () => {
    if (user.token) {
      axios
        .post(
          `${backend_url}/api/comment/unlike`,
          {
            comment_id: comment?.id,
          },
          {
            headers: {
              Accept: "application/json",
              Authorization: `Bearer ${user.token}`,
            },
          }
        )
        .then((response) => {
          setIsLikedByUser(false);
          setLikesCount((count) => count - 1);
        });
    }
  };

  return (
    <Button onClick={handleClick}>
      <Avatar
        src={`https://img.icons8.com/pastel-glyph/34/000000/facebook-like--v1.png`}
        style={{
          width: "23px",
          height: "23px",
          marginRight: "16px",
          alignSelf: "center",
          backgroundColor: isLikedByUser ? "#2d95b5" : "lightgrey",
          borderRadius: "50%",
          padding: "5px",
          cursor: "pointer",
        }}
      />

      {likesCount && (
        <>
          <Number>{likesCount}</Number>
          <p>Like{likesCount > 1 ? "s" : ""}</p>
        </>
      )}
      <LoginAlert
        // key={post.id}
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
        open={showLoginAlert}
        message="Please Login To Like The Post."
      />
    </Button>
  );
};

export default Likes;

const Button = withStyles({
  root: {
    maxWidth: "240px",
    display: "flex",
    alignItems: "center",
    marginRight: "4rem",
    cursor: "pointer",
    margin: "1rem 0rem 0.3rem 0rem",
  },
})(Box);

const Number = withStyles({
  root: {
    display: "inline-block",
    marginRight: 8,
  },
})(Box);

const LoginAlert = withStyles({
  root: {
    zIndex: 20000,
  },
})(Snackbar);
