import { Box, Typography, withStyles } from "@material-ui/core";
import React, { useContext } from "react";
import { QuestionContext } from "../SingleQuestion";

interface IProps {}

export default function Question({}: IProps) {
  const { question } = useContext(QuestionContext);

  return (
    <Container>
      <QuestionContainer>
        {question?.question && (
          <QuestionTitle variant="subtitle2">
            {question?.question}
          </QuestionTitle>
        )}
        {question?.image?.image_link && (
          <Avatar
            src={`${backend_url}/storage/${quizInfo?.image_link}`}
            variant="square"
            style={{ height: 100, width: 100 }}
          />
        )}
      </QuestionContainer>
    </Container>
  );
}

const Container = withStyles({
  root: {
    width: "100%",
    padding: "0.5rem",
  },
})(Box);

const QuestionTitle = withStyles({
  root: {},
})(Typography);

const QuestionContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
  },
})(Box);
