import { useState } from "react";
import useLocalState from "../../../custom-hooks/useLocalState";
import IQuestion from "../../../Models/IQuestion";

export default function useTestState(quizId: number, questions: IQuestion[]) {
  const [user] = useLocalState("user", "");
  const [answers, setAnswersState] = useLocalState(
    `${user?.email}-quiz${quizId}-answers`,
    {}
  );

  const [currentQuestionIndex, setCurrentQuestionIndex] = useLocalState(0);

  const setAnswers = (optionID: number, question: IQuestion, text?: string) => {
    setAnswersState((state: any) => {
      let tempAnswers = { ...state };

      if (question.answer_type === "multiple_choice") {
        const options = tempAnswers[`${question?.id}`];

        if (options && options.includes(optionID)) {
          tempAnswers = { ...state, [`${question?.id}`]: [] };
        } else {
          tempAnswers = { ...state, [`${question?.id}`]: [optionID] };
        }
      } else if (question?.answer_type === "checkboxes") {
        const options = tempAnswers[`${question?.id}`];

        if (options && options.includes(optionID)) {
          tempAnswers = {
            ...state,
            [`${question?.id}`]: options.filter(
              (option: number) => option != optionID
            ),
          };
        } else if (options && !options.includes(optionID)) {
          tempAnswers = {
            ...state,
            [`${question?.id}`]: [...options, optionID],
          };
        } else {
          tempAnswers = {
            ...state,
            [`${question?.id}`]: [optionID],
          };
        }
      } else if (question?.answer_type === "short_paragraph") {
        if (!text) return { ...tempAnswers };
        tempAnswers = {
          ...state,
          [`${question?.id}`]: text,
        };
      }

      return { ...tempAnswers };
    });
  };

  const goToNextQuestion = () => {
    if (currentQuestionIndex >= questions?.length - 1) return;
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const goToPreviousQuestion = () => {
    if (currentQuestionIndex <= 0) return;
    setCurrentQuestionIndex(currentQuestionIndex - 1);
  };

  const showPrevious: boolean = currentQuestionIndex != 0;
  const showNext: boolean = currentQuestionIndex < questions?.length - 1;

  return {
    answers,
    setAnswers,
    currentQuestionIndex,
    goToNextQuestion,
    goToPreviousQuestion,
    showPrevious,
    showNext,
  };
}
