import React, { useEffect, useState } from "react";

interface IProps {
  start_time: string;
  duration: string;
}

const Timer: React.FC<IProps> = ({ start_time, duration }) => {
  const [timeElapsed, setTimeElapsed] = useState<number>(
    getTimeElapsed(start_time)
  );

  useEffect(() => {
    setInterval(() => {}, 500);
  }, []);

  return <p>{timeElapsed}</p>;
};

export default Timer;

function getTimeElapsed(start_time: string): number {
  let start = new Date(`${start_time}`);
  let now = new Date();

  let timePassedInSeconds: number = (now.getTime() - start.getTime()) / 1000;

  return timePassedInSeconds;
}
