import { Box, Typography, withStyles } from "@material-ui/core";
import { useContext } from "react";
import { QuestionContext } from "../SingleQuestion";

interface IProps {}

export default function Question({}: IProps) {
  const { question } = useContext(QuestionContext);

  return (
    <Container>

      {question?.question && (
        <QuestionTitle variant="subtitle2">{question?.question}</QuestionTitle>
      )}
    </Container>
  );
}

const Container = withStyles({
  root: {
    width: "100%",
    padding: "0.5rem",
  },
})(Box);

const QuestionTitle = withStyles({
  root: {},
})(Typography);

const QuestionContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
  },
})(Box);
