import useLocalState from "./useLocalState";

export default function useOptionClicked(optionID: number, quizID: number) {
  const [user] = useLocalState("user", "");
  const [answers, setAnswers] = useLocalState("asdf", {});

  const handleClick = () => {
    console.log(
      `${user.email} has clicked option: ${optionID} of quiz: ${quizID}`
    );
  };

  return [handleClick];
}
