import { Avatar, Box, Snackbar, withStyles } from "@material-ui/core";
import axios from "axios";
import React, { useEffect, useState } from "react";
import useLocalState from "../../../../../custom-hooks/useLocalState";
import IPost from "../../../../../Models/IPost";
import { backend_url } from "../../../../../url";

interface IProps {
  post: IPost;
}

const Likes: React.FC<IProps> = ({ post }) => {
  const [user] = useLocalState("user", "");
  const [showLoginAlert, setShowLoginAlert] = useState<boolean>(false);
  const [isLikedByUser, setIsLikedByUser] = useState<boolean>(false);
  const [likesCount, setLikesCount] = useState<number>(post?.likes_count);

  const handleClick = () => {
    if (!user.token) {
      setShowLoginAlert(true);
      setTimeout(() => {
        setShowLoginAlert(false);
      }, 3000);

      return;
    }
  };

  useEffect(() => {
    getIfUserHasLikedThePost();
  }, []);

  useEffect(() => {
    getIfUserHasLikedThePost();
    if (!user.token) setIsLikedByUser(false);
  }, [user]);

  const getIfUserHasLikedThePost = () => {
    if (user.token) {
      axios
        .get(`${backend_url}/api/posts/${post?.slug}/is-liked-by-user`, {
          headers: {
            Accept: "application/json",
            Authorization: `Bearer ${user.token}`,
          },
        })
        .then((response) => {
          setIsLikedByUser(response.data.is_liked_by_user);
        });
    }
  };

  const likePost = () => {
    if (user.token) {
      axios
        .post(
          `${backend_url}/api/post/like`,
          {
            post_id: post?.id,
          },
          {
            headers: {
              Accept: "application/json",
              Authorization: `Bearer ${user.token}`,
            },
          }
        )
        .then((response) => {
          if (response.data == "liked") {
            setIsLikedByUser(true);
          }
        });
    }
  };

  return (
    <Button onClick={handleClick}>
      <Avatar
        src={`https://img.icons8.com/pastel-glyph/34/000000/facebook-like--v1.png`}
        style={{
          width: "23px",
          height: "23px",
          marginRight: "16px",
          alignSelf: "center",
          backgroundColor: isLikedByUser ? "#2d95b5" : "lightgrey",
          borderRadius: "50%",
          padding: "5px",
          cursor: "pointer",
        }}
      />

      {post.likes_count ? (
        <>
          <Number>{likesCou}</Number>
          <p>Like{post?.likes_count > 1 ? "s" : ""}</p>
        </>
      ) : (
        <>
          <Number>0</Number>
          <p>Like</p>
        </>
      )}
      <LoginAlert
        key={post.id}
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
        open={showLoginAlert}
        message="Please Login To Like The Post."
      />
    </Button>
  );
};

export default Likes;

const Button = withStyles({
  root: {
    maxWidth: "240px",
    display: "flex",
    alignItems: "center",
    marginRight: "16px",
  },
})(Box);

const Container = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
    cursor: "pointer",
  },
})(Box);

const Number = withStyles({
  root: {
    display: "inline-block",
    marginRight: 8,
  },
})(Box);

const LoginAlert = withStyles({
  root: {
    zIndex: 20000,
  },
})(Snackbar);
