import { useEffect, useState } from "react";
export default function useSingleQuestion(index, currentQuestionIndex) {
  const {
    0: isVisible,
    1: setIsVisible
  } = useState(index == currentQuestionIndex);
  const {
    0: direction,
    1: setDirection
  } = useState("right");
  useEffect(() => {
    if (currentQuestionIndex > index) {
      setDirection("right");
    } else if (currentQuestionIndex < index) {
      setDirection("left");
    }

    setIsVisible(index == currentQuestionIndex);
  }, [currentQuestionIndex]);
  return {
    isVisible,
    direction
  };
}
