import React from "react";

import { Box, Typography, withStyles } from "@material-ui/core";
import IOption from "../../../../Models/IOption";

import Image from "next/image";
import { local_backend_url } from "../../../../url";

interface IProps {
  option: IOption;
  position: number;
}

const Option: React.FC<IProps> = ({ option, position }) => {
  return (
    <Container>
      {option.image && (
        <AspectRatioContainer>
          <ImageContainer>
            <Image
              alt="image"
              src={`${local_backend_url}/storage/${option.image.image_link}`}
              height={"100%"}
              width={"100%"}
              layout="responsive"
            />
          </ImageContainer>
        </AspectRatioContainer>
      )}
      {/* {option.title && (
        <TextContainer>
          <OptionButton />
          <Typography>{`${String.fromCharCode(position + 64)}. ${
            option.title
          }`}</Typography>
        </TextContainer>
      )} */}
    </Container>
  );
};

export default Option;

const Container = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    minWidth: "48%",
  },
})(Box);

const AspectRatioContainer = withStyles({
  root: {
    height: 0,
    overflow: "hidden",
    paddingTop: "75%",
    background: "white",
    position: "relative",
 }
})(Box)

const ImageContainer = withStyles({
  root: {
    position: "absolute",
    top: 0,
    left: 0,
    height: "100%",
    width: "100%",
  },
})(Box);

const TextContainer = withStyles({
  root: {
    background:
      "transparent linear-gradient(180deg, #995FD4 0%, #1F29356E 100%) 0% 0% no-repeat padding-box",
    transform: "translateY(-100%)",
    paddingBottom: "25%",
  },
})(Box);

const OptionButton = withStyles({
  root: {},
})(Box);
