import React, { useEffect } from "react";

import { useRouter } from "next/router";
import axios from "axios";
import post from "../../UtilFunctions/post";
import { backend_url, local_backend_url } from "../../url";
import useUserCookie from "../../custom-hooks/useUserCookie";

interface IProps {}

const Google: React.FC<IProps> = () => {
  const router = useRouter();
  const { code } = router.query;

  useEffect(() => {
    if (code) {
      axios({
        method: "get",
        url: `/api/auth/google?get=user&code=${code}`,
      }).then((res) => {
        const id_token = res.data.user.data.id_token;

        axios({
          method: "GET",
          url: `https://oauth2.googleapis.com/tokeninfo?id_token=${id_token}`,
        }).then((response: any) => {
          console.log(response.data);
          const {
            email,
            email_verified,
            at_hash,
            name,
            picture,
          } = response.data;

          axios
            .post(`${local_backend_url}/api/auth/googleSignIn`, {
              id_token: id_token,
              email: email,
              at_hash: at_hash,
              email_verified: email_verified === "true" ? true : false,
              name: name,
              picture: picture,
            })
            .then((response) => {
              const token = response.data;

              const user = {
                email: email,
                name: name,
                picture: picture,
                token: token,
              };

              console.log(user);

              setUser(user);
            })
            .catch((error) => setUser(""));
        });
      });
    }
  }, [code]);

  return <p>hello</p>;
};

export default Google;
