import { useState } from "react";

const useLoginAlert = () => {
  const {
    0: showLoginAlert,
    1: setShowLoginAlert
  } = useState(false);

  const handleClickWhenLoggedOut = () => {
    setShowLoginAlert(true);
    setTimeout(() => {
      setShowLoginAlert(false);
    }, 3000);
  };

  const close = () => {
    setShowLoginAlert(false);
  };

  return {
    showLoginAlert,
    close,
    handleClickWhenLoggedOut
  };
};

export default useLoginAlert;
