import React, { useState } from "react";
import { makeStyles } from "@material-ui/core";
import SearchBox from "./SearchBox/SearchBox";
import ISearchResult from "../../../Models/ISearchResult";
import useSearch from "../../../custom-hooks/useSearch";

interface IProps {
  handleClose: Function;
}

interface IResult {
  posts: ISearchResult[];
  topics: ISearchResult[];
  tags: ISearchResult[];
}

const SearchModal: React.FC<IProps> = ({ handleClose }) => {
  const [query, setQuery] = useState<String>("");
  const classes = useStyles();

  const { results: data, loading, error } = useSearch(
    "/canvas-blog/api/search",
    query
  );

  return (
    <>
      <div className={classes.backdrop} onClick={() => handleClose()}></div>
      <div className={classes.container}>
        <SearchBox query={query} setQuery={setQuery} />
      </div>
    </>
  );
};

export default SearchModal;

const useStyles = makeStyles({
  backdrop: {
    position: "fixed",
    top: 0,
    right: 0,
    bottom: 0,
    left: 0,
    backgroundColor: "black",
    opacity: 0.6,
    zIndex: 1000,
  },
  container: {
    position: "absolute",
    top: 0,
    left: 0,
    zIndex: 2000,
    color: "black",
    width: "100vw",
    paddingInline: "20%",
    paddingTop: 50,
    ["@media (max-width: 700px)"]: {
      paddingInline: "10%",
    },
    ["@media (max-width: 400px)"]: {
      paddingInline: "5%",
    },
  },
});
