import React, { useEffect, useState } from "react";

interface IProps {
  start_time: string;
  duration: string;
}

const Timer: React.FC<IProps> = ({ start_time, duration }) => {
  const [timeElapsed, setTimeElapsed] = useState<string>(
    getTimeElapsed(start_time)
  );

  useEffect(() => {
    setInterval(() => {
      setTimeElapsed(getTimeElapsed(start_time));
    }, 500);
  }, []);

  useEffect()

  return <p>{`${timeElapsed}`}</p>;
};

export default Timer;

function getTimeElapsed(start_time: string): string {
  let start = new Date(`${start_time}`);
  let now = new Date();

  let timePassedInSeconds: number = (now.getTime() - start.getTime()) / 1000;
  timePassedInSeconds = Math.floor(timePassedInSeconds);

  return `${Math.floor(timePassedInSeconds / 60)}:${Math.floor(
    timePassedInSeconds % 60
  )}`;
}
