import React, { useEffect } from "react";

import { useRouter } from "next/router";
import axios from "axios";
import post from "../../UtilFunctions/post";
import { backend_url, local_backend_url } from "../../url";
import reactUseCookie from "react-use-cookie";
import useLocalState from "../../custom-hooks/useLocalState";
import { Box, LinearProgress, Typography, withStyles } from "@material-ui/core";

interface IProps {}

const Google: React.FC<IProps> = () => {
  const router = useRouter();
  const { code } = router.query;

  const [user, setUser] = useLocalState("user", "");

  useEffect(() => {
    if (code) {
      axios({
        method: "get",
        url: `/api/auth/google?get=user&code=${code}`,
      }).then((res) => {
        const id_token = res.data.user.data.id_token;

        axios({
          method: "GET",
          url: `https://oauth2.googleapis.com/tokeninfo?id_token=${id_token}`,
        }).then((response: any) => {
          console.log(response.data);
          const {
            email,
            email_verified,
            at_hash,
            name,
            picture,
          } = response.data;

          axios
            .post(`${local_backend_url}/api/auth/googleSignIn`, {
              id_token: id_token,
              email: email,
              at_hash: at_hash,
              email_verified: email_verified === "true" ? true : false,
              name: name,
              picture: picture,
            })
            .then((response) => {
              const token = response.data;

              const user: any = {
                email: email,
                name: name,
                picture: picture,
                token: token,
              };

              console.log(user);

              setUser(user);
            })
            .catch((error) => setUser(""));
        });
      });
    }
  }, [code]);

  return (
    <Container>
      <LinearProgress />
      <Title>Please wait while we sign you in...</Title>
    </Container>
  );
};

export default Google;

const Container = withStyles({
  root: {
    width: "100vw",
    display: "flex",
    alignItems: "center",
  },
})(Box);

const Title = withStyles({
  root: {},
})(Typography);
