import { Avatar, Box, Typography, withStyles } from "@material-ui/core";
import React from "react";
import IQuiz from "../../../Models/IQuiz";
import { backend_url } from "../../../url";

interface IProps {
  quiz: IQuiz;
}

const Quiz: React.FC<IProps> = ({ quiz }) => {
  console.log(quiz);

  return (
    <Container>
      <Avatar
        src={`${backend_url}/storage/${quiz?.image?.image_link}`}
        alt={quiz.title}
        style={{ width: 80, height: 80 }}
      />
      <TitleButtonContainer>
        <Title>{quiz.title}</Title>
        <Button>Take the Quiz</Button>
      </TitleButtonContainer>
    </Container>
  );
};

export default Quiz;

const Container = withStyles({
  root: {
    width: "80%",
    background:
      "transparent linear-gradient(180deg, #966BC2 0%, #437BBE6E 100%) 0% 0% no-repeat padding-box",
    marginTop: 30,
    borderRadius: 20,
    padding: "20px 50px",
    ["@media (max-width: 600px)"]: {
      width: "95%",
    },
    display: "flex",
  },
})(Box);

const TitleButtonContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    marginLeft: 50,
  },
})(Box);

const Title = withStyles({
  root: {
    fontSize: "1.2rem",
  },
})(Typography);

const Button = withStyles({ root: {
    width: 200,

} })(Box);
