import { Box, Typography, withStyles } from "@material-ui/core";
import { useContext } from "react";
import { QuizTestStateContext } from "../../Questions";

export default function QuestionCountAndTimer() {
  const { questionCount, currentQuestionIndex } = useContext(
    QuizTestStateContext
  );
  return (
    <Container>
      <QuestionCount variant="subtitle2">{`Q${
        currentQuestionIndex + 1
      } / ${questionCount}`}</QuestionCount>
      <TimeRemaining variant="caption">0:0</TimeRemaining>
    </Container>
  );
}

const Container = withStyles({
  root: {
    float: "left",
    display: "flex",
    alignItems: "center",
  },
})(Box);

const QuestionCount = withStyles({
  root: {
    marginRight: "0.5rem",
    fontWeight: 600,
  },
})(Typography);

const TimeRemaining = withStyles({
  root: {
    fontWeight: 600,
  },
})(Typography);
