import { Box, withStyles } from "@material-ui/core";
import React, { useState } from "react";
import IImage from "../../../../Models/IImage";

import Image from "next/image";
import { backend_url } from "../../../../url";

interface IProps {
  title: string;
  image: IImage;
}

const Title: React.FC<IProps> = ({ title, image }) => {
  const [imageUrl, setImageUrl] = useState<string>(image.image_link);
  return (
    <Container>
      <ImageContainer>
        <Image
          src={`${backend_url}/storage/${image.image_link}`}
          height={"100%"}
          width={"100%"}
        />
      </ImageContainer>
    </Container>
  );
};

export default Title;

const Container = withStyles({
  root: {
    width: "100%",
    display: "flex",
    alignItems: "center",
  },
})(Box);

const ImageContainer = withStyles({
  root: {
    height: 50,
    width: 50,
  },
})(Box);
