import { Box, CircularProgress, withStyles } from "@material-ui/core";
import useBadge from "./useBadge";

interface IProps {
  testId: number;
}

export default function Badge({ testId }: IProps) {
  const { isLoading } = useBadge(testId);

  return (
    <Container>
      {isLoading && (
        <LoadingContainer>
          <CircularProgress />

        </LoadingContainer>
      )}
    </Container>
  );
}

const Container = withStyles({
  root: {
    width: "100%",
    border: "1px solid #e6e6e4",
    borderRadius: "7px 7px 0px 0px",
    backgroundColor: "white",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
})(Box);

const LoadingContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "1rem",
  },
})(Box);
