import React from "react";

import { backend_url } from "../../../../../url";

interface IProps {
  src: string;
  caption?: string;
  isWide: boolean;
}

const PostBodyImage: React.FC<IProps> = ({ src, caption, isWide }) => {
  return <div>
      <Image src={`${backend_url}${src}`} width="100%" height="100%" />
  </div>;
};

export default PostBodyImage;
