  container: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "black",
    zIndex: 100,
    overFlowY: "scroll",
    overflowX: "hidden",
    opacity: 0.6,
  },
});

const SearchModal: React.FC<IProps> = ({ isShown, setIsShown }) => {
  const classes = useStyles();


  console.log(isShown)
  return (
    <>
      {isShown ? (
        <div
          className={classes.container}
          onClick={() => {
            console.log("clicked");
            setIsShown(false);
          }}
        ></div>
      ) : null}
    </>
  );
};

export default SearchModal;
