import {
  Avatar,
  Box,
  CircularProgress,
  Typography,
  withStyles,
} from "@material-ui/core";
import { backend_url } from "../../../../../../url";
import BottomBar from "./BottomBar/BottomBar";
import useQuizInfo from "./useQuizInfo";

import SubjectIcon from "@material-ui/icons/Subject";

interface IProps {
  quizId: number;
}

export default function QuizInfo({ quizId }: IProps) {
  const { testStatus, quizInfo, userInfo, isLoading } = useQuizInfo(quizId);

  return (
    <Container>
      {isLoading && (
        <LoadingContainer>
          <CircularProgress color="primary" />
        </LoadingContainer>
      )}
      <ImageContainer>
        <Avatar
          src={`${backend_url}/storage/${quizInfo?.image_link}`}
          variant="square"
          style={{ height: 100, width: 100 }}
        />
      </ImageContainer>
      {/* <Title variant="h5">{quizInfo?.title?.toLowerCase()}</Title> */}
      <Description variant="subtitle1">
        {quizInfo?.description?.toLowerCase()}
      </Description>
      <TestTakerCount variant="subtitle2">
        {quizInfo?.test_taker_count ?? "0"} people have taken the{" "}
        {quizInfo?.is_assessment ? "assessment" : "quiz"}
      </TestTakerCount>
      <QuizDetailContainer>
        <QuizDetail>
          <SubjectIcon />
          <QuizDetailText variant="subtitle1">
            <b>{quizInfo?.questions_count ?? "0"} questionsi</b>
          </QuizDetailText>
        </QuizDetail>
      </QuizDetailContainer>
      <BottomBar testStatus={testStatus} />
    </Container>
  );
}

const Title = withStyles({
  root: {
    margin: "1rem 0 0 0",
    textTransform: "capitalize",
    fontWeight: 600,
  },
})(Typography);

const Description = withStyles({
  root: {
    margin: "0.2rem 0",
    textTransform: "capitalize",
  },
})(Typography);

const TestTakerCount = withStyles({
  root: {
    margin: "0.2rem 0",
    color: "gray",
  },
})(Typography);

const Container = withStyles({
  root: {
    padding: "1rem",
    display: "flex",
    flexDirection: "column",
  },
})(Box);

const LoadingContainer = withStyles({
  root: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
  },
})(Box);

const ImageContainer = withStyles({
  root: {},
})(Box);

const QuizDetailContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    margin: "1rem 0",
  },
})(Box);

const QuizDetail = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
  },
})(Box);

const QuizDetailText = withStyles({
  root: {},
})(Typography);
