import React, { useContext, useEffect, useState } from "react";

import { Box, makeStyles, Typography, withStyles } from "@material-ui/core";
import IOption from "../../../../Models/IOption";

import Image from "next/image";
import { local_backend_url } from "../../../../url";
import { AnswersContext } from "../../Question";

interface IProps {
  option?: IOption;
  position?: number;
  isChecked?: boolean;
}

const useStyles = makeStyles({
  container: {
    display: "flex",
    alignItems: "center",
    marginBottom: "1rem",
    cursor: "pointer",
  },
});

const Option: React.FC<IProps> = ({ option, position }) => {
  const { answers, setAnswers, getIsChecked } = useContext(AnswersContext);

  const [isChecked, setIsChecked] = useState<boolean>(getIsChecked(option.id));

  const classes = useStyles({ isChecked });

  useEffect(() => {
    console.log(
      `${option.title}:${option.id} is ${
        getIsChecked(option.id) ? "checked" : "unchecked"
      }`
    );
    setIsChecked(getIsChecked(option.id));
  }, [answers]);

  return (
    <Container onClick={() => setAnswers(option.id)}>
      {option.image && (
        <ImageContainer>
          <Image
            alt="image"
            src={`${local_backend_url}/storage/${option.image.image_link}`}
            height={"100%"}
            width={"100%"}
            layout="responsive"
          />
        </ImageContainer>
      )}
      {option.title && (
        <TextContainer>
          <OptionButton />
          <Typography>
            {`${String.fromCharCode(position + 64)}. ${option.title}`}
          </Typography>
        </TextContainer>
      )}
      {isChecked && "checked"}
    </Container>
  );
};

export default Option;

const Container = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
    marginBottom: "1rem",
    cursor: "pointer",
  },
})(Box);

const ImageContainer = withStyles({
  root: {
    height: 100,
    width: 100,
    borderRadius: 5,
    marginRight: "2rem",
    overflow: "hidden",
    flexShrink: 0,
  },
})(Box);

const TextContainer = withStyles({
  root: {
    background:
      "transparent linear-gradient(180deg, #995FD4 0%, #1F29356E 100%) 0% 0% no-repeat padding-box",
    width: "100%",
    padding: "1rem 1rem",
    borderRadius: 10,
    display: "flex",
    alignItems: "center",
  },
})(Box);

const OptionButton = withStyles({
  root: {
    padding: "0.7rem",
    backgroundColor: "#17ABC2",
    borderRadius: "50%",
    overflow: "hidden",
    marginRight: "1rem",
  },
})(Box);
