import { Avatar, Box, withStyles } from "@material-ui/core";
import React, { useState } from "react";
import useLocalState from "../../../../custom-hooks/useLocalState";

import Image from "next/image";

interface IProps {
  postId: string;
}

const CommentBox: React.FC<IProps> = ({ postId }) => {
  const [user] = useLocalState("user", "");
  const [comment, setComment] = useState<string>("");

  const handleSubmit = () => {};

  const handleChange = e => {

  }

  return (
    <Container>
      <Avatar
        src={user?.picture}
        style={{
          width: 35,
          height: 35,
          borderRadius: "50%",
          marginRight: "16px",
        }}
      />
      <input
        type="text"
        placeholder="Tell us what you think about this post..."
        value={comment}
        onChange={handleChange}
        style={{
          width: "100%",
          padding: "12px",
          marginRight: "16px",
          border: "1px solid black",
        }}
      />
      <ImageContainter>
        <Image
          src={`/assets/send_icon.svg`}
          width={25}
          height={25}
          onClick={handleSubmit}
        />
      </ImageContainter>
    </Container>
  );
};

export default CommentBox;

const Container = withStyles({
  root: {
    width: "100%",
    display: "flex",
    marginTop: "1.5rem",
    alignItems: "center",
    justifyContent: "space-between",
  },
})(Box);

const ImageContainter = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
    cursor: "pointer",
  },
})(Box);
