export default function getFormattedTime(
  time: String
): {
  hours: number;
  minutes: number;
  seconds: number;
} {
  let hours = 0,
    minutes = 0,
    seconds = 0;



  const times = time?.split(":");

  seconds = parseInt(times[times?.length - 1]);
  if (times.length > 1) {
    minutes = parseInt(times[times?.length - 2]);
  }

  if (times.length > 2) {
    hours = parseInt(times[times?.length - 3]);
  }

  return {
    hours: hours,
    minutes: minutes,
    seconds: seconds,
  };
}
