import { Box, withStyles } from "@material-ui/core";
import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import useLocalState from "../../../../custom-hooks/useLocalState";
import { backend_url } from "../../../../url";
import { CommentsContext, CommentsCountContext } from "../SinglePost";
import Comment from "./Comment/Comment";

interface IProps {}

const CommentsSection = () => {
  const { comments, postId } = useContext(CommentsContext);
  const { commentsCount } = useContext(CommentsCountContext);

  const [user] = useLocalState("user", "");

  useEffect(() => {
    getLikedCommentsIds();
  })

  const getLikedCommentsIds = () => {
    if (user.token) {
      axios
        .get(`${backend_url}/api/posts/${postId}/liked-comments-ids`, {
          headers: {
            Accept: "application/json",
            Authorization: `Bearer ${user.token}`,
          },
        })
        .then((response) => {
          console.log(response.data);
        });
    }
  };

  return (
    <Container>
      <Heading>Comments({commentsCount})</Heading>
      {comments &&
        comments.map((comment) => {
          return <Comment comment={comment} />;
        })}
    </Container>
  );
};

export default CommentsSection;

const Heading = withStyles({
  root: {
    color: "grey",
    fontWeight: 700,
  },
})(Box);

const Container = withStyles({
  root: {
    margin: "2rem 0rem",
  },
})(Box);
